<?php
/**
 * Featured images for posts table
 *
 */


 /**
 * We define the columns that the table will have
 */
 function KTT_add_post_featured_image_column_head( $defaults ) {

     /**
     * We are going to form a nuvo array with all the columns
     */
     $result = array();
     $n = 0;



     /**
     * We walk through each of the columns
     */
     if ($defaults) foreach ($defaults as $key => $value) {

       if ($n == 1) $result["featured_image"] = '<span style="opacity:0.5" class="dashicons dashicons-format-image" title="Featured image"><span class="screen-reader-text">Featured Image</span></span>';

         /**
         * We add the column to the new arrays
         */
         $result[$key] = $value;

         $n += 1;

     }

     /**
     * We return the new arrays
     */
   	return $result;

 }
 add_filter('manage_posts_columns', 'KTT_add_post_featured_image_column_head');




 /**
 * We define the columns that the table will have
 */
 function KTT_add_post_featured_image_column( $column_name, $post_ID ) {

     /**
     * We get the full post
     */
     $post = KTT_get_post($post_ID);

     /**
     * We identify the columns
     */
     if ($column_name == 'featured_image') {

       $post_featured_image_thumb = "data:image/png;base64,";
       $featured_image_id = get_post_thumbnail_id( $post->ID );
       $image_attributes = wp_get_attachment_image_src( $featured_image_id, 'thumbnail' );
       if ($image_attributes) $post_featured_image_thumb = $image_attributes[0];

       echo '<span style="border-radius:3px;display:block;height:80px;width:80px;background-color:#f0f0f0;"><img alt="image" style="margin:0;padding:0;border-radius:2px;max-width:80px;max-height:80px;" src="' . $post_featured_image_thumb . '"></span>';


     }


 }
 add_action('manage_posts_custom_column', 'KTT_add_post_featured_image_column', 10, 2);




add_action('admin_head', 'KTT_post_featured_image_column_width');

function KTT_post_featured_image_column_width() {
    echo '<style type="text/css">';
    echo '.column-featured_image { width:80px; }';
    echo '</style>';
}
