<?php
/**
 * Featured images for categories
 *
 */

 // add featured image option to post_tag admin pages

 $args                          	 = array();
 $args['taxmeta_taxonomy']        = 'post_tag';
 $args['taxmeta_id']              = KTT_var_name('post_tag_featured_image');
 $args['taxmeta_name']            = esc_html__('Featured Image', 'narratium');
 $args['taxmeta_description']     = esc_html__('Attach a featured image for the tag.', 'narratium');
 $args['taxmeta_type']            = 'image';

 $KTT_new_taxonomy_meta = new KTT_new_taxonomy_meta($args);





 // show the featured image in the categories list

 function KTT_manage_my_post_tag_columns($columns) {
    // add 'My Column'
    $result = array();
    if ($columns) foreach ($columns as $column_key => $column) {
      $result[$column_key] = $column;
      if ($column_key == 'cb') $result['featured_image'] = 'Image';
    }
    return $result;;
 }
 add_filter('manage_edit-post_tag_columns','KTT_manage_my_post_tag_columns');



 function KTT_manage_post_tag_custom_fields($deprecated,$column_name,$term_id) {
      if ($column_name == 'featured_image') {
        $post_featured_image_thumb = "data:image/png;base64,";
       $featured_image_id = get_taxonomy_meta($term_id, KTT_var_name('post_tag_featured_image'), true);
       $image_attributes = wp_get_attachment_image_src( $featured_image_id, 'thumbnail' );
         if ($image_attributes) $post_featured_image_thumb = $image_attributes[0];

        	   echo '<span style="border-radius:3px;display:block;height:80px;width:80px;background-color:#f0f0f0;"><img alt="image" style="margin:0;padding:0;border-radius:2px;max-width:80px;max-height:80px;" src="' . $post_featured_image_thumb . '"></span>';
      }
 }
 add_filter ('manage_post_tag_custom_column', 'KTT_manage_post_tag_custom_fields', 10,3);
