<?php
/**
* This script is responsible for adding a topbar to our site that we will use as an interface help for general navigation on the web, here you can integrate navigation elements and logos, helps, etc.
*/




/**
* this function generates the layout of the topbar
*/
function KTT_theme_topbar_layout() {
    ?>
    <div data-layout-padding class="header-menu-wrap min-width-300px position-fixed top-0 left-0 z-index-20">

      <?php do_action('KTT_add_theme_topbar_field');?>

    </div>
    <?php
}






/**
* This function is responsible for adding the logo to the top bar
*/
function KTT_theme_topbar_site_logo() {

    /**
    * We get the logo url
    */
    $logo_image_url = esc_url(get_site_icon_url());

    /**
    * If we do not have a logo image we put the theme logo by default
    */
    if (!$logo_image_url) $logo_image_url = "data:image/png;base64,";

    /**
    * We obtain the preferred mode that defines the behavior of the logo with the menu
    */
    $logo_menu_behaviour = get_option(KTT_theme_var_name('site_menu_behaviour'), "logo_and_menu_handle");

    ?>

    <md-button
    id="open-site-menu"
    md-no-ink
    aria-label="Menu"
    <?php if ($logo_menu_behaviour == 'logo_as_menu_handle') {?>ng-click="open_left_sidenav()"<?php } ?>
    <?php if ($logo_menu_behaviour != 'logo_as_menu_handle') {?>ng-href="<?php echo esc_url(home_url("/"));?>"<?php } ?>
    style="background-image:url('<?php echo esc_attr($logo_image_url);?>');background-position:center center;background-size:cover"
    class="button-behaviour site-menu-handle-open site-logo-button md-fab site-palette-yang-1-color site-palette-yang-1-background-color" >
    </md-button>

    <?php if ($logo_menu_behaviour != 'logo_as_menu_handle') { ?>

    <span
    data-ng-click="open_left_sidenav()"
    class="ng-cloak menu-handle cursor-pointer button-behaviour display-inline-block box-shadow site-menu-handle-open padding-left-20 padding-right-10 text-size-xsmall position-relative site-palette-yin-2-color site-palette-yang-4-background-color border-radius-4">
      <i class="ng-cloak material-icons">menu</i>
      <span class="ng-cloak"><?php esc_html_e("Menu", 'narratium');?></span>
    </span>

    <?php } ?>


    <?php
}
add_action('KTT_add_theme_topbar_field', 'KTT_theme_topbar_site_logo', 1);




/**
* This hook is responsible for closing the menu in case it is open after an ajax call
*/
function KTT_fix_close_site_menu_overlay() {
  ?>
  jQuery('.site-menu-handle-close').hide();
  jQuery('.site-menu-handle-open').show();
  <?php

}
add_action('KTT_theme_ajax_load_content_finally', 'KTT_fix_close_site_menu_overlay');







/**
* This general function the general layout of the topbar
*/
function KTT_add_site_topbar() {

    /**
    * We call the function that is responsible for generating the layout
    */
    KTT_theme_topbar_layout();

}
add_action('KTT_theme_body_start', 'KTT_add_site_topbar', 5);
 ?>
